import React, { useEffect, useState } from "react";
import Charts from "./Charts";
import DataTable from "./DataTable";
import 'bootstrap/dist/css/bootstrap.min.css';

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchData = async () => {
    const res = await fetch("http://localhost:5000/get_nap");
    const json = await res.json();
    setData(json);
  };

  const scrapeNow = async () => {
    setLoading(true);
    await fetch("http://localhost:5000/scrape_now");
    await fetchData();
    setLoading(false);
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="container py-5">
      <h2 className="mb-4 text-center">ðŸ“° Geo TV News Scraper Dashboard</h2>

      <div className="d-flex justify-content-center mb-4">
        <button className="btn btn-primary me-3" onClick={scrapeNow} disabled={loading}>
          {loading ? "Scraping..." : "Scrape Now"}
        </button>
        <button className="btn btn-success" onClick={fetchData}>Refresh</button>
      </div>

      <Charts data={data} />
      <hr className="my-5" />
      <DataTable data={data} />
    </div>
  );
};

export default Dashboard;
