import React from "react";
import { Bar } from "react-chartjs-2";
import { Chart as ChartJS, BarElement, CategoryScale, LinearScale } from "chart.js";

ChartJS.register(BarElement, CategoryScale, LinearScale);

const generateChartData = (label, rawData, color) => {
  const counts = {};
  rawData.forEach(item => {
    item[label]?.forEach(val => {
      counts[val] = (counts[val] || 0) + 1;
    });
  });

  return {
    labels: Object.keys(counts),
    datasets: [{
      label: `${label} Mentions`,
      data: Object.values(counts),
      backgroundColor: color,
    }]
  };
};

const Charts = ({ data }) => {
  const authorCounts = {};
  data.forEach(item => {
    authorCounts[item.name] = (authorCounts[item.name] || 0) + 1;
  });

  return (
    <div className="row">
      <div className="col-md-4">
        <Bar data={{
          labels: Object.keys(authorCounts),
          datasets: [{
            label: "Author Mentions",
            data: Object.values(authorCounts),
            backgroundColor: "#0d6efd"
          }]
        }} />
      </div>
      <div className="col-md-4">
        <Bar data={generateChartData("area", data, "#dc3545")} />
      </div>
      <div className="col-md-4">
        <Bar data={generateChartData("person", data, "#198754")} />
      </div>
    </div>
  );
};

export default Charts;
